import React from 'react';
import styled from 'styled-components';
// eslint-disable-next-line import/no-cycle
import { useSelector } from 'react-redux';
// eslint-disable-next-line import/no-cycle
import Nav from './Nav';
import MakImg from '../images/intro-sec1.png';
import TrashBinImg from '../images/trash-bin.png';

const Mypage = () => {
  const state = useSelector(states => states.reviewReducer); // 로그인리듀서로 바꿔야
  const { isLogin } = state;

  const makImg = MakImg;
  const trashBinImg = TrashBinImg;

  // eslint-disable-next-line no-unused-vars
  const nicknameClickHandler = e => {
    // eslint-disable-next-line no-console
    // eslint-disable-next-line no-alert
    alert('edit nickname');
  };

  return (
    // {!(isLogin)
    //   ?
    // (
    //   ''
    // ) : (
    <div>
      <Nav />
      <Section>
        <MyProfile>
          <MyProfileTitle>MY&nbsp;PROFILE</MyProfileTitle>
          <MyProfileList>Nickname</MyProfileList>
          <MyProfileContent>willy LEE</MyProfileContent>
          {/* // eslint-disable-next-line react/button-has-type */}
          {/* <button>edit</button> */}
          <MyProfileEditBtn onclick={nicknameClickHandler}>
            edit
          </MyProfileEditBtn>
          <MyProfileList>Email</MyProfileList>
          <MyProfileContent>willy@gmail.com</MyProfileContent>
          <MyProfileList>Registered Date</MyProfileList>
          <MyProfileContent>2020-02-02</MyProfileContent>
        </MyProfile>

        <MyProfileHello>Hello #username! Wellcome back!</MyProfileHello>

        <MyBtn>
          <MyReviewBtn>MY REVIEWS</MyReviewBtn>
          <MyFavBtn>MY FAVORITES</MyFavBtn>
        </MyBtn>

        <MyReviews>
          <MyReviewsBox>
            <MyReviewImg src={makImg} alt="makImg" />
            <MyReviewContent>
              <MyReviewCreatedAt>2020-02-02</MyReviewCreatedAt>
              <MyReviewReview>야미!dfdddddfdfdfdffdfdfd</MyReviewReview>
              <MyReviewBtnBox>
                <MyReviewEditBtn>edit</MyReviewEditBtn>
                {/* edit을 click하면, review는 그대로 있고 input으로..... */}
                <MyReviewDeleteBtn>delete</MyReviewDeleteBtn>
              </MyReviewBtnBox>
            </MyReviewContent>
          </MyReviewsBox>
        </MyReviews>

        <MyFavs>
          <MyFavsBox>
            <MyFavImg src={makImg} alt="makImg" />
            <MyFavImg src={makImg} alt="makImg" />
            <MyFavImg src={makImg} alt="makImg" />
            <MyFavImg src={makImg} alt="makImg" />
            <MyFavImg src={makImg} alt="makImg" />
            <MyFavImg src={makImg} alt="makImg" />
          </MyFavsBox>
          <MyFavDeleteBtn src={trashBinImg} alt="trashBin" />
        </MyFavs>
      </Section>
    </div>
    //   );
    // };
  );
};

const Section = styled.section`
  display: flex;
  flex-direction: column;
  margin: 0;
  @media screen and (min-width: 480px) and (max-width: 768px) {
    // 테블릿
    display: flex;
    flex-direction: column;
    margin: 0px;
  }
  @media screen and (min-width: 769px) {
    // 데스크탑
    display: flex;
    flex-direction: column;
    margin-left: 50px;
  }
`;
const MyProfile = styled.div`
  display: flex;
  flex-direction: column;
  width: 100vw;
  height: 100vh;
  margin: 0px;
  font-size: 20px;
  background: linear-gradient(to bottom, #c29b86, white);
  @media screen and (min-width: 480px) and (max-width: 768px) {
    // 테블릿
    display: flex;
    flex-direction: column;
    /* background-color : #C29B86; */
    background-color: orange;
    width: 100vw;
    height: 100vh;
    margin: 0px;
    font-size: 20px;
  }
  @media screen and (min-width: 769px) {
    // 데스크탑
    display: flex;
    flex-direction: column;
    background-color: #c29b86;
    width: 19vw;
    height: 50vh;
    margin-left: 9rem;
    margin-top: 40px; // header height ref
    margin-bottom: 6rem;
    font-size: 20px;
  }
`;
const MyProfileTitle = styled.div`
  display: flex;
  flex-direction: row;
  color: #e7d1bf;
  font-size: 2.5em;
  align-items: center;
  justify-content: center;
  margin-top: 40%;
  margin-bottom: 15%;
  @media screen and (min-width: 480px) and (max-width: 768px) {
    // 테블릿
    display: flex;
    text-indent: none;
    margin-top: 40%;
    margin-bottom: 15%;
    font-size: 3em;
  }
  @media screen and (min-width: 769px) {
    // 데스크탑
    display: flex;
    flex-direction: row;
    text-indent: 2em;
    margin-top: 20%;
    margin-bottom: 30%;
    font-size: 3em;
    text-indent: 4em;
  }
`;
const MyProfileList = styled.li`
  display: flex;
  flex-direction: column;
  list-style: none;
  color: #293848;
  background-color: pink;
  font-weight: bold;
  align-items: center;
  font-size: 1em;
`;
const MyProfileContent = styled.li`
  display: flex;
  flex-direction: column;
  list-style: none;
  color: black;
  font-weight: bold;
  align-items: center;
  font-size: 0.9em;
  margin: 0.5em;
`;
const MyProfileEditBtn = styled.button`
  display: flex;
  justify-content: center;
  background: #293848;
  color: white;
`;

const MyProfileHello = styled.div`
  display: flex;
  color: deeppink;
`;

const MyBtn = styled.div`
  display: flex;
  flex-direction: row;
  /* align-items : baseline; */
  /* align-content : baseline; */

  /* flex-wrap : wrap; */
`;
const MyReviewBtn = styled.button`
