// `;

const StyleStars = styled(BsStarFill)`
  width: 20px;
  height: auto;

  @media ${props => props.theme.mobile} {
  }

  @media ${props => props.theme.tablet} {
    width: 25px;
    height: auto;
  }

  @media ${props => props.theme.desktop} {
  }
`;

export default StarInput;
