import styled from 'styled-components';
import star from '../images/star.png';

const ReviewInput = () => {
  return (
    <StyleInputBox>
      <div>
        <StyleStarBox>
          <StyleStar />
          <StyleStar />
          <StyleStar />
          <StyleStar />
          <StyleStar />
        </StyleStarBox>
        <StyleInputWrap>
          <StyleInputItem>
            <StyleInput placeholder="리뷰를 입력해주세요(300자 이내)" />
          </StyleInputItem>
          <StyleBtnItem>
            <StyleSaveBtn>SAVE</StyleSaveBtn>
          </StyleBtnItem>
        </StyleInputWrap>
        <StyleBtnWrap>
          <StyleBtnArea>
            <StyleBtn>photo</StyleBtn>
          </StyleBtnArea>
        </StyleBtnWrap>
      </div>
    </StyleInputBox>
  );
};

const StyleInputBox = styled.div`
  width: 100vw;
  padding-top: 10vmin;
  display: flex;
